    });
  });


  document.getElementById('page_num').textContent = num;
}



function queueRenderPage(num) {
  if (pageRendering) {
    pageNumPending = num;
  } else {
    renderPage(num);
  }
}



if (hidePaginator) {
  document.getElementById("paginator").style.display = 'none';
}



